import React, { useState, useLayoutEffect } from "react";

import { LIGHT, DARK, DARKER } from "../enums/theme";
import { lightTheme, darkTheme, darkerTheme } from "../theme";

function getTheme(themeName) {
  switch (themeName) {
    case LIGHT:
      return lightTheme;
    case DARK:
      return darkTheme;
    case DARKER:
      return darkerTheme;
    default:
      return lightTheme;
  }
}

function useTheme() {
  const [themeName, setThemeName] = useState();
  const [theme] = useState();

  useLayoutEffect(() => {
    if (localStorage.getItem("theme")) {
      console.log("localStorage");
      setThemeName(localStorage.getItem("theme"));
    } else if (
      window.matchMedia &&
      window.matchMedia("(prefers-color-scheme: dark)").matches
    ) {
      console.log("matchesMedia");
      setThemeName(DARK);
    } else {
      setThemeName(LIGHT);
    }
  }, []);

  useEffect(() => {
    getTheme(themeName);
  }, [themeName]);

  const onSetThemeName = themeName => {
    localStorage.setItem("theme", themeName);
    setThemeName(themeName);
  };

  return { theme, themeName, setThemeName: onSetThemeName };
}

export default useTheme;
