import styled from "styled-components";

export const ButtonContainer = styled.ul`
  list-style-type: none;
  display: flex;
  flex-direction: row;
  li {
    button {
      background: transparent;
      outline: transparent;
      border: none;
      color: white;
    }
  }
`;
